@import 'variables';

body, html {
    height: 100%;
}

body {
    margin: 0;

    #bg{
        width: 100%;
        height: 100%;
        background-color: color(primary);
        position: absolute;
        z-index: -1;
        clip-path: polygon(100% 0, 100% 82%, 45% 100%, 0 100%, 0 0);

        @include viewLarge{
            clip-path: polygon(0 0, 75% 0, 55% 100%, 0% 100%);
        }

    }

    header a{
        color: #fff;
        text-decoration: none;
        padding: $padding;
        display: block;
        text-transform: uppercase;
    }
}

main{

    @include viewLarge{
        display: grid;
        grid-template-columns: 50% auto;
        grid-template-areas: "primary card";
    }

    section#card{
        background: #fff;
        padding: 20px;
        margin: 1em auto;
        border-radius: $borders;
        width: 80%;
        box-shadow: 0 10px 30px rgba(0,0,0,.2);


        @include viewLarge {
            grid-area: card;
            height: fit-content;
            align-self: center;
            margin: 1em;
        }

        ul {

            list-style: none;
            margin: 0;
            padding: 0;

            li{
                margin-bottom: 10px;


                span{
                    position: absolute;
                    width: 30px;
                    height: 30px;
                    background-color: color(azul-brillo);
                    border-radius: 50%;
                    margin-right: 10px;
                }

                strong{
                    display: inline-block;
                    margin-left: 40px;
                    margin-top: 10px;
                }

            }
        }

    }


    section#primary{
        color: #fff;
        padding: $padding;
        text-align: center;

        h1{
            font-size: 3em;
            margin-top: 10px;
            text-transform: uppercase;
        }

        p{
            font-size: 1.4em;
        }

        a{
            color: color(azul-oscuro);
            border-radius: $borders;
            text-decoration: none;
            text-transform: uppercase;
            font-weight: bold;
            text-align: center;
            padding: $padding;
            margin: 50px auto 0 auto;
            background-color: color(acento);
        }

        button{


            background-color: color(primary);
            &:hover{
                background-color: color(azul-brillo);
            }

        }

    }

}

sources.css/_variables.scss:
$colors: (
        primary: #005DFF,
        azul-brillo: lighten(#005DFF, 40%),
        azul-oscuro: darken(#005DFF, 40%),
        acento: #FFF6BB
);

$breakpoints: (
        small: 0px,
        medium: 767px,
        large: 1200px
);

$padding: 15px;
$borders: 15px;

@function color($color-name){
  @return map-get($colors, $color-name)
}

@mixin viewport($breakpoint){
  @if map-has-key($breakpoints, $breakpoint){
    @media (screen and min-width: #{map-get($breakpoints, $breakpoint)}) {
      @content
    }
  }
  @else{
    @warn "No existe Viewport `#{$breakpoint}` "
  }
}

@mixin viewLarge {
  @media (min-width: #{map-get($breakpoints, large)}){
    @content
  }
}
